import{_ as t,c as s,o as a,aM as i}from"./chunks/framework.IHAHILrl.js";const d=JSON.parse('{"title":"Assistant Basics","description":"","frontmatter":{"title":"Assistant Basics","date":"2025-09-02T01:16:27.000Z"},"headers":[],"relativePath":"docs/assistant/basics.md","filePath":"docs/assistant/basics.md","lastUpdated":1763177628000}'),o={name:"docs/assistant/basics.md"};function n(r,e,l,p,g,c){return a(),s("div",null,[...e[0]||(e[0]=[i(`<h1 id="assistant-basics" tabindex="-1">Assistant Basics <a class="header-anchor" href="#assistant-basics" aria-label="Permalink to &quot;Assistant Basics&quot;">‚Äã</a></h1><p>Assistants are the heart of Kelivo. They bundle AI model settings, prompts, and behaviors into reusable configurations that you can customize for different tasks and workflows.</p><h2 id="what-is-an-assistant" tabindex="-1">What is an Assistant? <a class="header-anchor" href="#what-is-an-assistant" aria-label="Permalink to &quot;What is an Assistant?&quot;">‚Äã</a></h2><p>An Assistant is a pre-configured AI persona with:</p><ul><li><strong>Identity</strong>: Name and avatar for easy recognition</li><li><strong>Model Selection</strong>: Default provider and model</li><li><strong>Behavior Settings</strong>: Temperature, top-p, and other parameters</li><li><strong>Instructions</strong>: System prompt defining role and expertise</li><li><strong>Capabilities</strong>: Optional tools like memory, search, TTS, and MCP</li></ul><p>Think of assistants as specialized team members - each with unique skills, personalities, and access to different tools.</p><h2 id="why-use-assistants" tabindex="-1">Why Use Assistants? <a class="header-anchor" href="#why-use-assistants" aria-label="Permalink to &quot;Why Use Assistants?&quot;">‚Äã</a></h2><h3 id="benefits" tabindex="-1">Benefits <a class="header-anchor" href="#benefits" aria-label="Permalink to &quot;Benefits&quot;">‚Äã</a></h3><ul><li><strong>Consistency</strong>: Same behavior across all conversations</li><li><strong>Efficiency</strong>: Switch between specialized personas instantly</li><li><strong>Organization</strong>: Separate work, personal, and creative tasks</li><li><strong>Customization</strong>: Fine-tune settings for specific use cases</li><li><strong>Reusability</strong>: Create once, use across multiple chats</li></ul><h3 id="example-use-cases" tabindex="-1">Example Use Cases <a class="header-anchor" href="#example-use-cases" aria-label="Permalink to &quot;Example Use Cases&quot;">‚Äã</a></h3><ul><li><strong>Code Reviewer</strong>: GPT-4o with strict, analytical prompts for reviewing pull requests</li><li><strong>Creative Writer</strong>: Claude 3.5 Sonnet with high temperature for storytelling</li><li><strong>Research Assistant</strong>: Gemini Pro with web search enabled for finding information</li><li><strong>Language Tutor</strong>: Any model with patient, educational prompts and TTS</li><li><strong>Data Analyst</strong>: o1-preview for complex reasoning about datasets</li></ul><h2 id="creating-an-assistant" tabindex="-1">Creating an Assistant <a class="header-anchor" href="#creating-an-assistant" aria-label="Permalink to &quot;Creating an Assistant&quot;">‚Äã</a></h2><h3 id="step-by-step-guide" tabindex="-1">Step-by-Step Guide <a class="header-anchor" href="#step-by-step-guide" aria-label="Permalink to &quot;Step-by-Step Guide&quot;">‚Äã</a></h3><ol><li><p><strong>Open Assistants</strong></p><ul><li>Navigate to the <strong>Assistants</strong> section from the main menu</li><li>Tap the <strong>+</strong> or <strong>Create New Assistant</strong> button</li></ul></li><li><p><strong>Set Identity</strong></p><ul><li><strong>Name</strong>: Choose a descriptive name (e.g., &quot;Python Expert&quot;, &quot;Marketing Guru&quot;)</li><li><strong>Avatar</strong>: Select an icon or upload a custom image</li><li><strong>Description</strong> (optional): Add notes about the assistant&#39;s purpose</li></ul></li><li><p><strong>Select AI Model</strong></p><ul><li><strong>Provider</strong>: Choose from configured providers (OpenAI, Google, Anthropic, etc.)</li><li><strong>Model</strong>: Select the specific model to use <ul><li><strong>Fast models</strong>: gpt-3.5-turbo, gemini-1.5-flash, claude-3-haiku</li><li><strong>Powerful models</strong>: gpt-4o, gemini-1.5-pro, claude-3.5-sonnet</li><li><strong>Reasoning models</strong>: o1-preview, o1-mini</li></ul></li></ul></li><li><p><strong>Configure Parameters</strong></p><p><strong>Temperature</strong> (0.0 - 2.0):</p><ul><li><code>0.0 - 0.3</code>: Deterministic, factual (code, math, analysis)</li><li><code>0.7</code>: Balanced (recommended default)</li><li><code>1.0 - 1.5</code>: Creative (writing, brainstorming)</li></ul><p><strong>Top-p</strong> (0.0 - 1.0):</p><ul><li><code>0.9</code>: Recommended default</li><li>Lower values: More focused responses</li><li>Higher values: More diverse outputs</li></ul><p><strong>Top-k</strong> (optional):</p><ul><li>Usually 40-50 (provider-dependent)</li><li>Not all providers support this parameter</li></ul></li><li><p><strong>Write System Prompt</strong></p><ul><li>Define the assistant&#39;s role and expertise</li><li>Set behavioral guidelines</li><li>Specify output format preferences</li><li>See <a href="/docs/assistant/prompts">Prompts</a> for best practices</li></ul></li><li><p><strong>Enable Tools</strong> (optional)</p><ul><li><strong>Memory</strong>: Remember context across sessions</li><li><strong>Web Search</strong>: Access current information</li><li><strong>TTS</strong>: Read responses aloud</li><li><strong>MCP</strong>: Connect external tools</li></ul></li><li><p><strong>Save</strong></p><ul><li>Review all settings</li><li>Tap <strong>Save</strong> or <strong>Create</strong></li></ul></li></ol><h3 id="quick-example" tabindex="-1">Quick Example <a class="header-anchor" href="#quick-example" aria-label="Permalink to &quot;Quick Example&quot;">‚Äã</a></h3><p><strong>Name</strong>: Code Mentor<br><strong>Model</strong>: GPT-4o<br><strong>Temperature</strong>: 0.3<br><strong>Top-p</strong>: 0.9<br><strong>System Prompt</strong>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>You are an expert software engineer and coding mentor. Provide clear, </span></span>
<span class="line"><span>well-commented code examples with explanations. Always consider edge </span></span>
<span class="line"><span>cases, performance, and best practices. When reviewing code, be </span></span>
<span class="line"><span>constructive and educational.</span></span></code></pre></div><p><strong>Tools</strong>: None</p><h2 id="core-configuration-fields" tabindex="-1">Core Configuration Fields <a class="header-anchor" href="#core-configuration-fields" aria-label="Permalink to &quot;Core Configuration Fields&quot;">‚Äã</a></h2><h3 id="name-avatar" tabindex="-1">Name &amp; Avatar <a class="header-anchor" href="#name-avatar" aria-label="Permalink to &quot;Name &amp; Avatar&quot;">‚Äã</a></h3><p><strong>Purpose</strong>: Visual identification in the app</p><p><strong>Tips</strong>:</p><ul><li>Use clear, descriptive names</li><li>Choose distinct avatars for easy recognition</li><li>Consider using emojis for quick visual scanning (e.g., üêç Python Helper)</li></ul><h3 id="default-provider-model" tabindex="-1">Default Provider &amp; Model <a class="header-anchor" href="#default-provider-model" aria-label="Permalink to &quot;Default Provider &amp; Model&quot;">‚Äã</a></h3><p><strong>Purpose</strong>: Which AI service and model to use</p><p><strong>Choosing a Model</strong>:</p><ul><li><strong>For speed</strong>: gpt-3.5-turbo, gemini-1.5-flash, claude-3-haiku</li><li><strong>For quality</strong>: gpt-4o, gemini-1.5-pro, claude-3.5-sonnet</li><li><strong>For reasoning</strong>: o1-preview, o1-mini</li><li><strong>For cost</strong>: Flash/mini variants</li><li><strong>For vision</strong>: Ensure multimodal support (GPT-4o, Gemini, Claude 3+)</li></ul><p><strong>Fallback</strong>: If a model is unavailable, Kelivo may prompt you to select an alternative.</p><h3 id="temperature-top-p-top-k" tabindex="-1">Temperature / Top-p / Top-k <a class="header-anchor" href="#temperature-top-p-top-k" aria-label="Permalink to &quot;Temperature / Top-p / Top-k&quot;">‚Äã</a></h3><p><strong>Purpose</strong>: Control response style and creativity</p><p><strong>Common Configurations</strong>:</p><ul><li><strong>Coding assistant</strong>: temp=0.2, top-p=0.9</li><li><strong>General chat</strong>: temp=0.7, top-p=0.9</li><li><strong>Creative writing</strong>: temp=1.2, top-p=0.95</li><li><strong>Factual Q&amp;A</strong>: temp=0.3, top-p=0.85</li></ul><p><strong>Tip</strong>: Start with defaults (temp=0.7, top-p=0.9) and adjust based on results.</p><h3 id="system-prompt" tabindex="-1">System Prompt <a class="header-anchor" href="#system-prompt" aria-label="Permalink to &quot;System Prompt&quot;">‚Äã</a></h3><p><strong>Purpose</strong>: Define role, behavior, and constraints</p><p><strong>Key Elements</strong>:</p><ol><li><strong>Role</strong>: &quot;You are a [profession/expert]&quot;</li><li><strong>Expertise</strong>: Specific knowledge areas</li><li><strong>Behavior</strong>: Tone, formality, response style</li><li><strong>Format</strong>: How to structure outputs</li><li><strong>Constraints</strong>: What to avoid or limitations</li></ol><p><strong>Example</strong>:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>You are a friendly fitness coach specializing in home workouts. </span></span>
<span class="line"><span>Provide exercise recommendations based on available equipment and </span></span>
<span class="line"><span>fitness level. Always prioritize safety and proper form. Be </span></span>
<span class="line"><span>encouraging and motivational.</span></span></code></pre></div><p>See <a href="/docs/assistant/prompts">Prompts documentation</a> for advanced techniques.</p><h3 id="tools-integrations" tabindex="-1">Tools &amp; Integrations <a class="header-anchor" href="#tools-integrations" aria-label="Permalink to &quot;Tools &amp; Integrations&quot;">‚Äã</a></h3><p><strong>Memory</strong>:</p><ul><li>Stores summaries of important facts</li><li>Recalls context in future conversations</li><li>See <a href="/docs/assistant/memory">Memory documentation</a></li></ul><p><strong>Web Search</strong>:</p><ul><li>Automatically searches for current information</li><li>Requires search provider configuration</li><li>Best for news, facts, and research tasks</li></ul><p><strong>TTS (Text-to-Speech)</strong>:</p><ul><li>Reads responses aloud</li><li>Customizable voice and speed</li><li>Useful for accessibility and multitasking</li></ul><p><strong>MCP (Model Context Protocol)</strong>:</p><ul><li>Connect external tools and APIs</li><li>File system, databases, custom functions</li><li>See <a href="/docs/assistant/mcp">MCP documentation</a></li></ul><h2 id="per-chat-overrides" tabindex="-1">Per-Chat Overrides <a class="header-anchor" href="#per-chat-overrides" aria-label="Permalink to &quot;Per-Chat Overrides&quot;">‚Äã</a></h2><p>You can override assistant settings for specific conversations without changing the assistant defaults.</p><h3 id="how-to-override" tabindex="-1">How to Override <a class="header-anchor" href="#how-to-override" aria-label="Permalink to &quot;How to Override&quot;">‚Äã</a></h3><ol><li><strong>Open a chat</strong> with an assistant</li><li><strong>Access chat settings</strong> (usually via menu or settings icon)</li><li><strong>Modify parameters</strong>: <ul><li>Switch to a different model</li><li>Adjust temperature</li><li>Enable/disable tools</li></ul></li><li><strong>Continue chatting</strong> with new settings</li></ol><p><strong>Use Cases</strong>:</p><ul><li>Testing different models</li><li>Temporary creativity boost (higher temp)</li><li>One-off tasks requiring special settings</li></ul><p><strong>Note</strong>: Overrides apply only to that specific chat. The assistant&#39;s default settings remain unchanged.</p><h2 id="managing-assistants" tabindex="-1">Managing Assistants <a class="header-anchor" href="#managing-assistants" aria-label="Permalink to &quot;Managing Assistants&quot;">‚Äã</a></h2><h3 id="editing" tabindex="-1">Editing <a class="header-anchor" href="#editing" aria-label="Permalink to &quot;Editing&quot;">‚Äã</a></h3><ol><li>Open <strong>Assistants</strong> list</li><li>Tap on an assistant to edit</li><li>Modify any settings</li><li><strong>Save</strong> changes</li></ol><p>All future chats will use updated settings. Existing chats may continue with original settings unless overridden.</p><h3 id="duplicating" tabindex="-1">Duplicating <a class="header-anchor" href="#duplicating" aria-label="Permalink to &quot;Duplicating&quot;">‚Äã</a></h3><ol><li>Select an assistant</li><li>Tap <strong>Duplicate</strong> or <strong>Copy</strong></li><li>Modify the copy as needed</li><li><strong>Save</strong> as a new assistant</li></ol><p><strong>Use Case</strong>: Create variations for A/B testing or different tasks.</p><h3 id="deleting" tabindex="-1">Deleting <a class="header-anchor" href="#deleting" aria-label="Permalink to &quot;Deleting&quot;">‚Äã</a></h3><ol><li>Select an assistant</li><li>Tap <strong>Delete</strong> or trash icon</li><li><strong>Confirm</strong> deletion</li></ol><p><strong>Warning</strong>: Deleting an assistant doesn&#39;t delete associated chats, but those chats may lose assistant-specific settings.</p><h3 id="exporting-importing" tabindex="-1">Exporting/Importing <a class="header-anchor" href="#exporting-importing" aria-label="Permalink to &quot;Exporting/Importing&quot;">‚Äã</a></h3><ul><li><strong>Export</strong>: Share assistant configurations as JSON files</li><li><strong>Import</strong>: Load assistants from files</li></ul><p><strong>Use Cases</strong>:</p><ul><li>Backup your configurations</li><li>Share with team members</li><li>Transfer between devices</li></ul><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">‚Äã</a></h2><h3 id="naming-conventions" tabindex="-1">Naming Conventions <a class="header-anchor" href="#naming-conventions" aria-label="Permalink to &quot;Naming Conventions&quot;">‚Äã</a></h3><ul><li>Use descriptive names: &quot;Python Tutor&quot; vs &quot;Assistant 1&quot;</li><li>Include specialization: &quot;SEO Content Writer&quot;, &quot;SQL Database Expert&quot;</li><li>Add context if needed: &quot;Work - Email Drafter&quot;, &quot;Personal - Recipe Finder&quot;</li></ul><h3 id="organization" tabindex="-1">Organization <a class="header-anchor" href="#organization" aria-label="Permalink to &quot;Organization&quot;">‚Äã</a></h3><ul><li>Create assistants for distinct tasks, not every conversation</li><li>Limit to 5-10 core assistants for most users</li><li>Use folders/tags if available in future versions</li></ul><h3 id="optimization" tabindex="-1">Optimization <a class="header-anchor" href="#optimization" aria-label="Permalink to &quot;Optimization&quot;">‚Äã</a></h3><ul><li><strong>Start simple</strong>: Basic prompt, standard settings</li><li><strong>Iterate</strong>: Refine based on actual usage</li><li><strong>Test variations</strong>: Duplicate and experiment with parameters</li><li><strong>Monitor costs</strong>: Track token usage per assistant</li></ul><h3 id="system-prompt-guidelines" tabindex="-1">System Prompt Guidelines <a class="header-anchor" href="#system-prompt-guidelines" aria-label="Permalink to &quot;System Prompt Guidelines&quot;">‚Äã</a></h3><ul><li>Be specific but concise</li><li>Use numbered rules for clarity</li><li>Include examples for complex behaviors</li><li>Update prompts based on performance</li></ul><h3 id="memory-usage" tabindex="-1">Memory Usage <a class="header-anchor" href="#memory-usage" aria-label="Permalink to &quot;Memory Usage&quot;">‚Äã</a></h3><ul><li>Enable memory for long-term projects or personal assistants</li><li>Disable for one-off tasks or privacy-sensitive conversations</li><li>Periodically review and clear outdated memories</li></ul><h2 id="common-patterns" tabindex="-1">Common Patterns <a class="header-anchor" href="#common-patterns" aria-label="Permalink to &quot;Common Patterns&quot;">‚Äã</a></h2><h3 id="the-specialist" tabindex="-1">The Specialist <a class="header-anchor" href="#the-specialist" aria-label="Permalink to &quot;The Specialist&quot;">‚Äã</a></h3><p><strong>Purpose</strong>: Expert in one specific domain<br><strong>Example</strong>: &quot;React Developer&quot; with coding-focused prompt, low temperature<br><strong>Tools</strong>: None or MCP for code execution</p><h3 id="the-creative" tabindex="-1">The Creative <a class="header-anchor" href="#the-creative" aria-label="Permalink to &quot;The Creative&quot;">‚Äã</a></h3><p><strong>Purpose</strong>: Imaginative, open-ended responses<br><strong>Example</strong>: &quot;Story Writer&quot; with high temperature, creative prompt<br><strong>Tools</strong>: None</p><h3 id="the-researcher" tabindex="-1">The Researcher <a class="header-anchor" href="#the-researcher" aria-label="Permalink to &quot;The Researcher&quot;">‚Äã</a></h3><p><strong>Purpose</strong>: Finding and synthesizing information<br><strong>Example</strong>: &quot;Web Researcher&quot; with web search enabled<br><strong>Tools</strong>: Web Search, Memory</p><h3 id="the-personal-assistant" tabindex="-1">The Personal Assistant <a class="header-anchor" href="#the-personal-assistant" aria-label="Permalink to &quot;The Personal Assistant&quot;">‚Äã</a></h3><p><strong>Purpose</strong>: Remembers your preferences and context<br><strong>Example</strong>: &quot;My AI&quot; with memory enabled, balanced settings<br><strong>Tools</strong>: Memory, TTS</p><h3 id="the-polyglot" tabindex="-1">The Polyglot <a class="header-anchor" href="#the-polyglot" aria-label="Permalink to &quot;The Polyglot&quot;">‚Äã</a></h3><p><strong>Purpose</strong>: Multi-language communication and translation<br><strong>Example</strong>: &quot;Translator&quot; with language-specific prompt<br><strong>Tools</strong>: TTS for pronunciation</p><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">‚Äã</a></h2><h3 id="assistant-isn-t-behaving-as-expected" tabindex="-1">Assistant isn&#39;t behaving as expected <a class="header-anchor" href="#assistant-isn-t-behaving-as-expected" aria-label="Permalink to &quot;Assistant isn&#39;t behaving as expected&quot;">‚Äã</a></h3><ul><li><strong>Review system prompt</strong>: Ensure it&#39;s clear and specific</li><li><strong>Check temperature</strong>: Too high may cause randomness</li><li><strong>Verify model</strong>: Some models handle instructions differently</li><li><strong>Test in new chat</strong>: Old context may influence behavior</li></ul><h3 id="responses-are-inconsistent" tabindex="-1">Responses are inconsistent <a class="header-anchor" href="#responses-are-inconsistent" aria-label="Permalink to &quot;Responses are inconsistent&quot;">‚Äã</a></h3><ul><li><strong>Lower temperature</strong>: Reduces randomness</li><li><strong>Strengthen system prompt</strong>: Add explicit rules</li><li><strong>Use examples</strong>: Few-shot prompts improve consistency</li></ul><h3 id="assistant-is-slow" tabindex="-1">Assistant is slow <a class="header-anchor" href="#assistant-is-slow" aria-label="Permalink to &quot;Assistant is slow&quot;">‚Äã</a></h3><ul><li><strong>Switch to faster model</strong>: Flash/Haiku variants</li><li><strong>Reduce context</strong>: Clear old messages</li><li><strong>Check provider status</strong>: May be experiencing delays</li></ul><h3 id="can-t-create-more-assistants" tabindex="-1">Can&#39;t create more assistants <a class="header-anchor" href="#can-t-create-more-assistants" aria-label="Permalink to &quot;Can&#39;t create more assistants&quot;">‚Äã</a></h3><ul><li><strong>Check limits</strong>: Some apps may have maximum assistant counts</li><li><strong>Delete unused ones</strong>: Free up slots</li><li><strong>Export before deleting</strong>: Backup configurations</li></ul><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">‚Äã</a></h2><ul><li>Learn to write effective <a href="/docs/assistant/prompts">Prompts</a></li><li>Explore <a href="/docs/assistant/memory">Memory</a> for context retention</li><li>Configure <a href="/docs/assistant/custom-requests">Custom Requests</a> for advanced setups</li><li>Set up <a href="/docs/assistant/mcp">MCP</a> for tool integration</li></ul><p>Need help? Check the <a href="/docs/getting-started/faq">FAQ</a> or <a href="https://github.com/Chevey339/kelivo/issues" target="_blank" rel="noreferrer">open an issue</a>.</p>`,104)])])}const h=t(o,[["render",n]]);export{d as __pageData,h as default};
